export default () => {
  return new Router({
    routes,
    mode: 'history',
    // 基准路径，所有路由都是在该路径下
    // base: '/base/'
    scrollBehavior (to, from, savedPosition) {
      // 路由跳转的时候页面要不要滚动
      if (savedPosition) {
        return savedPosition
      } else {
        return { x: 0, y: 0 }
      }
    },
